SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ie_status_laudo ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

/**
     Retorna a descricao do status do Laudo
        1 - Acesso via web
        2 - Marcado como lido na web
        3 - Marcado como n√£o lido na web
*/
ie_status_w  	varchar(1) := '3';
dt_liberacao_w	timestamp;


BEGIN
	select 	a.ie_status
	into STRICT 	ie_status_w
	from  	laudo_acesso_web a
	where  	a.nr_seq_laudo  =  nr_sequencia_p
	and    	a.nr_sequencia  =  ( SELECT  max(b.nr_sequencia)
				 from    laudo_acesso_web b );

	select 	dt_liberacao
	into STRICT	dt_liberacao_w
	from	laudo_paciente
	where	nr_sequencia 	=	nr_sequencia_p;

	if (coalesce(dt_liberacao_w::text, '') = '')	then
		ie_status_w := '3';
	end if;

RETURN ie_status_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
