CREATE OR REPLACE FUNCTION obter_ie_texto_grupo (nr_seq_laudo_questao_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_texto_grupo_w		varchar(15);


BEGIN

if (nr_seq_laudo_questao_item_p IS NOT NULL AND nr_seq_laudo_questao_item_p::text <> '') then

	select 	g.ie_texto_grupo
	into STRICT	ie_texto_grupo_w
	from 	laudo_questao_item l,
		item_questao_laudo i,
		grupo_questao_laudo g
	where 	l.nr_sequencia 	= nr_seq_laudo_questao_item_p
	and 	i.nr_sequencia 	= l.nr_seq_item_questao
	and	g.nr_sequencia 	= i.nr_seq_grupo;

end if;

return	ie_texto_grupo_w;

end;
$body$
