CREATE OR REPLACE FUNCTION obter_ie_tipo_ordem_nf (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ie_tipo_ordem_compra_w	varchar(2);


BEGIN 
if (nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '') then 
	begin 
	select	coalesce(ie_tipo_ordem,'N') 
