CREATE OR REPLACE FUNCTION obter_ie_tipo_os (ie_origem_p text, ie_classificacao_p text, ie_classificacao_cliente_p text, nr_seq_localizacao_p bigint, nr_seq_equipamento_p bigint, dt_ordem_servico_p timestamp) RETURNS varchar AS $body$
DECLARE

	ie_tipo_ordem_w 	man_ordem_servico.ie_tipo_ordem%type;

BEGIN
	select max(b.IE_TIPO_ORDEM)
		into STRICT ie_tipo_ordem_w	
		from (SELECT IE_TIPO_ORDEM
				from MAN_REGRA_TIPO_ORDEM a
				where coalesce(a.ie_origem, ie_origem_p) = coalesce(ie_origem_p, a.ie_origem)
				and coalesce(a.ie_classificacao, ie_classificacao_p)  = coalesce(ie_classificacao_p, a.ie_classificacao)
				and coalesce(a.ie_classificacao_cliente, ie_classificacao_cliente_p) = coalesce(ie_classificacao_cliente_p, a.ie_classificacao_cliente)
				and coalesce(a.nr_seq_localizacao, nr_seq_localizacao_p) = coalesce(nr_seq_localizacao_p, a.nr_seq_localizacao)
				and coalesce(a.nr_seq_equipamento, nr_seq_equipamento_p) = coalesce(nr_seq_equipamento_p, a.nr_seq_equipamento)
				and (a.ie_implantacao = 'S' and exists (select 1 from man_localizacao_status b
									where b.nr_seq_localizacao = nr_seq_localizacao_p 
									and 	dt_ordem_servico_p <= coalesce(b.dt_fim, clock_timestamp())))
				order by coalesce(nr_seq_localizacao, 99999999999), coalesce(nr_seq_equipamento, 99999999999), coalesce(ie_classificacao_cliente, 'ZZ')) b LIMIT 1;
						
	if (coalesce(ie_tipo_ordem_w::text, '') = '') then
		select max(b.IE_TIPO_ORDEM)
		into STRICT ie_tipo_ordem_w	
		from (SELECT IE_TIPO_ORDEM
				from MAN_REGRA_TIPO_ORDEM a
				where coalesce(a.ie_origem, ie_origem_p) = coalesce(ie_origem_p, a.ie_origem)
				and coalesce(a.ie_classificacao, ie_classificacao_p) = coalesce(ie_classificacao_p, a.ie_classificacao)
				and coalesce(a.ie_classificacao_cliente, ie_classificacao_cliente_p) = coalesce(ie_classificacao_cliente_p, a.ie_classificacao_cliente)
				and coalesce(a.nr_seq_localizacao, nr_seq_localizacao_p) = coalesce(nr_seq_localizacao_p, a.nr_seq_localizacao)
				and coalesce(a.nr_seq_equipamento, nr_seq_equipamento_p) = coalesce(nr_seq_equipamento_p, a.nr_seq_equipamento)
				and coalesce(a.ie_implantacao, 'N') = 'N'
				order by coalesce(nr_seq_localizacao, 99999999999), coalesce(nr_seq_equipamento, 99999999999), coalesce(ie_classificacao_cliente, 'ZZ')) b LIMIT 1;
	end if;
	
	return ie_tipo_ordem_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
