CREATE OR REPLACE FUNCTION obter_igs_tasymed_js ( dt_us_p timestamp, dt_evolucao_p timestamp, qt_ig_sem_us_p bigint, qt_ig_dia_us_p bigint) RETURNS bigint AS $body$
DECLARE


dt_tot_us	timestamp;
dif_datas	bigint;



BEGIN

dt_tot_us 	:= dt_us_p - ((qt_ig_sem_us_p * 7) + qt_ig_dia_us_p);

dif_datas	:= dt_evolucao_p - dt_tot_us;

return	dif_datas;

end;
