SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_imagem_ant_heg (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
qt_rx_w		bigint;
qt_UltraSom_w	bigint;
qt_tomo_w	bigint;
ds_retorno_w	varchar(255);
ds_nehum_w	varchar(40) := '[X] Nenhum';
ds_rx_w		varchar(40) := '[X] RX';
ds_Ultrasom_w	varchar(40) := '[X] Ultra-Som';
ds_Tomo_w	varchar(40) := '[X] Tomografia';


BEGIN 
 
select	count(*) 
into STRICT	qt_rx_w 
from	procedimento c, 
	procedimento_paciente b, 
	atendimento_paciente a 
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	a.nr_atendimento	= b.nr_atendimento 
and	b.cd_procedimento	= c.cd_procedimento 
and	b.ie_origem_proced	= c.ie_origem_proced 
and	c.cd_tipo_procedimento	= 4;
 
select	count(*) 
into STRICT	qt_UltraSom_w 
from	procedimento c, 
	procedimento_paciente b, 
	atendimento_paciente a 
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	a.nr_atendimento	= b.nr_atendimento 
and	b.cd_procedimento	= c.cd_procedimento 
and	b.ie_origem_proced	= c.ie_origem_proced 
and	c.cd_tipo_procedimento	= 2;
 
select	count(*) 
into STRICT	qt_tomo_w 
from	procedimento c, 
	procedimento_paciente b, 
	atendimento_paciente a 
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	a.nr_atendimento	= b.nr_atendimento 
and	b.cd_procedimento	= c.cd_procedimento 
and	b.ie_origem_proced	= c.ie_origem_proced 
and	c.cd_tipo_procedimento	= 3;
 
if (qt_rx_w <> 0) or (qt_UltraSom_w <> 0) or (qt_tomo_w <> 0 ) then 
	ds_nehum_w	:= '[ ] Nenhum';
end if;
 
if (qt_rx_w = 0) then 
	ds_rx_w		:= '[ ] RX';
end if;
 
if (qt_UltraSom_w = 0) then 
	ds_Ultrasom_w	:= '[ ] Ultra-Som';
end if;
 
if (qt_tomo_w = 0) then 
	ds_Tomo_w	:= '[ ] Tomografia';
end if;
 
ds_retorno_w	:= ds_nehum_w||'   '||ds_rx_w||'   '||ds_Ultrasom_w||'   '||ds_Tomo_w;
 
RETURN	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
