SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_imagem_relatorio (ds_caminho_delphi_p text, ds_caminho_java_p text) RETURNS varchar AS $body$
DECLARE


ds_ambiente_w	varchar(255);
ds_caminho_w	varchar(255);


BEGIN

select	program
into STRICT	ds_ambiente_w
from	v$session
where	audsid = (SELECT userenv('sessionid') );

if (upper(ds_ambiente_w) = 'TASY.EXE') then

	ds_caminho_w := ds_caminho_delphi_p;
else
	ds_caminho_w := ds_caminho_java_p;
end if;

return	ds_caminho_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
