CREATE OR REPLACE FUNCTION obter_imposto_repasse (nr_repasse_terceiro_p bigint) RETURNS bigint AS $body$
DECLARE


vl_imposto_w	double precision;


BEGIN

	select	sum(b.vl_imposto)
	into STRICT	vl_imposto_w
	from	repasse_terc_venc_trib b,
		repasse_terceiro_venc a
	where	a.nr_repasse_terceiro	= nr_repasse_terceiro_p
	and	a.nr_sequencia		= b.nr_seq_rep_venc;

return	vl_imposto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
