CREATE OR REPLACE FUNCTION obter_impressora_setor ( cd_setor_atendimento_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_impressora_w		varchar(80);
ie_padrao_w			varchar(1);

C01 CURSOR FOR
SELECT	substr(obter_desc_impressora(nr_seq_impressora),1,80),
		coalesce(ie_impressora_default,'N')
from	setor_impressora
where	cd_setor_atendimento = cd_setor_atendimento_p
and		((coalesce(cd_perfil,0) = 0) or (cd_perfil = cd_perfil_p))
order by	coalesce(nr_seq_apresentacao,9999999) desc,
			coalesce(cd_perfil,99999),
			dt_atualizacao_nrec desc;


BEGIN

open C01;
loop
fetch C01 into
	ds_impressora_w,
	ie_padrao_w;
EXIT WHEN NOT FOUND or (ie_padrao_w = 'S');  /* apply on C01 */
end loop;
close C01;

return	ds_impressora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
