SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inconsistencias_farmacia (nr_prescricao_p bigint, nr_sequencia_p bigint, ie_acao_p text) RETURNS varchar AS $body$
DECLARE


ie_consiste_w	varchar(1) := 'N';
nr_seq_evento_w	bigint;
qt_registro_w	bigint;
ds_retorno_w	varchar(2000) := null;
ie_suspenso_w	varchar(1) := 'N';

expressao1_w	varchar(255) := obter_desc_expressao_idioma(714564, null, wheb_usuario_pck.get_nr_seq_idioma);--É necessário registrar a(as) seguite(es) inconsistência(as)
c01 CURSOR FOR
	SELECT	nr_seq_evento
	from	consiste_alter_farm_item a,
		regra_consiste_alter_farm b
	where	b.nr_sequencia 		= a.nr_seq_regra
	and	b.ie_acao 		= ie_acao_p
	and	coalesce(b.ie_situacao,'A') 	= 'A';


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (nr_sequencia_p > 0) then
		select	coalesce(max(ie_suspenso),'N')
		into STRICT	ie_suspenso_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_sequencia_p;
	end if;
	if (ie_suspenso_w = 'N') then
		select	count(*)
		into STRICT	qt_registro_w
		from	prescr_material_incon_farm a
		where	a.nr_seq_inconsistencia = nr_seq_evento_w
		and	a.nr_prescricao		= nr_prescricao_p
		and	((a.nr_seq_material	= nr_sequencia_p) or (nr_sequencia_p = 0))
		and	coalesce(a.ie_situacao,'A')	= 'A'
		and	exists (	SELECT	1
					from 	prescr_material b
					where 	b.nr_prescricao = a.nr_prescricao
					and 	b.nr_sequencia = a.nr_seq_material
					and 	coalesce(b.ie_suspenso,'N') = 'N');

		if (qt_registro_w = 0) then
			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := expressao1_w || ' ' ||chr(10)||chr(13) ||
						substr(obter_dados_inconsist_farm(nr_seq_evento_w,'D'),1,255);
			else
				ds_retorno_w := ds_retorno_w ||chr(10)||chr(13) || substr(obter_dados_inconsist_farm(nr_seq_evento_w,'D'),1,255);
			end if;
		end if;
	end if;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
