SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inconsistencias_pepo (nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(550);
ds_inconsistencia_w		varchar(255) := '';

C01 CURSOR FOR
SELECT	obter_desc_expressao(b.cd_exp_consistencia,b.ds_consistencia)
from	consistencia_cirurgia a,
		consistencia_lib_pepo b
where	a.nr_seq_consistencia	= b.nr_sequencia
and		a.nr_seq_pepo 			= nr_seq_pepo_p
order by	b.nr_sequencia;

C02 CURSOR FOR
SELECT	obter_desc_expressao(b.cd_exp_consistencia,b.ds_consistencia)
from	consistencia_cirurgia a,
		consistencia_lib_pepo b
where	a.nr_seq_consistencia	= b.nr_sequencia
and		a.nr_cirurgia			= nr_cirurgia_p
order by	b.nr_sequencia;


BEGIN

if (coalesce(nr_seq_pepo_p,0) > 0) then
	open c01;
	loop
	fetch c01 into
		ds_inconsistencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (length(ds_retorno_w || ds_inconsistencia_w) < 550) then
			ds_retorno_w := substr(ds_retorno_w || ds_inconsistencia_w || '; ',1,500);
		end if;

		end;
	end loop;
	close c01;
elsif (coalesce(nr_cirurgia_p,0) > 0) then
	open c02;
	loop
	fetch c02 into
		ds_inconsistencia_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		if (length(ds_retorno_w || ds_inconsistencia_w) < 550) then
			ds_retorno_w := substr(ds_retorno_w || ds_inconsistencia_w || '; ',1,550);
		end if;

		end;
	end loop;
	close c02;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
