SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inconsistencia_farmacia ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_inconsistencia_w			varchar(2000);
nr_seq_inconsistencia_w		bigint;
ds_inconsistencia_aux_w		inconsistencia_med_farm.ds_inconsistencia%type;

c01 CURSOR FOR
SELECT	nr_seq_inconsistencia
from	prescr_material_incon_farm
where	nr_prescricao		= nr_prescricao_p
and		nr_seq_material		= nr_sequencia_p
and		coalesce(ie_situacao,'A')	= 'A';


BEGIN

select	max(nr_seq_inconsistencia)
into STRICT	nr_seq_inconsistencia_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and		nr_sequencia	= nr_sequencia_p;

if (nr_seq_inconsistencia_w IS NOT NULL AND nr_seq_inconsistencia_w::text <> '') then
	select	max(ds_inconsistencia)
	into STRICT	ds_inconsistencia_w
	from	inconsistencia_med_farm
	where	nr_sequencia	=	nr_seq_inconsistencia_w;
else

	open C01;
	loop
	fetch C01 into
		nr_seq_inconsistencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	max(ds_inconsistencia)
		into STRICT	ds_inconsistencia_aux_w
		from	inconsistencia_med_farm
		where	nr_sequencia	=	nr_seq_inconsistencia_w;

		if (coalesce(ds_inconsistencia_w::text, '') = '') then
			ds_inconsistencia_w	:= ds_inconsistencia_aux_w;
		else
			ds_inconsistencia_w	:= substr(ds_inconsistencia_w||';'||ds_inconsistencia_aux_w,1,2000);
		end if;

		end;
	end loop;
	close C01;

end if;

return	ds_inconsistencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
