CREATE OR REPLACE FUNCTION obter_inconsist_ocup_atend ( nr_seq_interno_p bigint, dt_entrada_p timestamp, dt_saida_p timestamp, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE



nr_atendimento_w	bigint;
dt_entrada_w		varchar(21);
dt_saida_w		varchar(21);
ds_retorno_w		varchar(4000) := '';

C01 CURSOR FOR
	SELECT	a.nr_atendimento,
		to_char(a.dt_entrada_unidade,'dd/mm/yyyy hh24:mi:ss'),
		to_char(a.dt_saida_interno,'dd/mm/yyyy hh24:mi:ss')
	from	setor_atendimento s,
		atend_paciente_unidade a
	where	a.cd_setor_atendimento	= s.cd_setor_atendimento
	and	a.cd_setor_atendimento 	= cd_setor_atendimento_p
	and	a.cd_unidade_basica 	= cd_unidade_basica_p
	and	a.cd_unidade_compl  	= cd_unidade_compl_p
	and	a.nr_seq_interno 	<> nr_seq_interno_p
	and	s.cd_classif_setor 		in (3,4)
	and	coalesce(s.ie_ocup_hospitalar,'N')	= 'S'
	and (a.dt_entrada_unidade between dt_entrada_p and	dt_saida_p
	or	coalesce(a.dt_saida_interno,clock_timestamp()) between dt_entrada_p and dt_saida_p)
	and	a.ie_passagem_setor = 'N'
	order by
		a.dt_entrada_unidade desc;


BEGIN

open C01;
loop
fetch C01 into
	nr_atendimento_w,
	dt_entrada_w,
	dt_saida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (length(ds_retorno_w) < 3900) or (coalesce(ds_retorno_w::text, '') = '')	then

		if (dt_saida_w  =  '30/12/2999 00:00:00') then
			dt_saida_w := '         -         ';
		end if;

		ds_retorno_w :=  lpad(nr_atendimento_w,8) || '       ' || dt_entrada_w || '         '  || dt_saida_w || chr(13) || chr(10) || ds_retorno_w;

	end if;
	end;
end loop;
close C01;

if (ds_retorno_w <> ' ') then
	ds_retorno_w	:= ds_retorno_w;
else
	ds_retorno_w := null;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
