CREATE OR REPLACE FUNCTION obter_inconsist_rem_escrit ( nr_seq_tit_rec_cobr_p bigint, nr_seq_banco_escrit_p bigint, nr_titulo_pagar_p bigint) RETURNS varchar AS $body$
DECLARE


ie_inconsistente_w	varchar(1)	:= 'N';
qt_registro_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
nr_seq_banco_escrit_w	bigint;


BEGIN

if (nr_seq_tit_rec_cobr_p IS NOT NULL AND nr_seq_tit_rec_cobr_p::text <> '') then

	null;

elsif (nr_seq_banco_escrit_p IS NOT NULL AND nr_seq_banco_escrit_p::text <> '') and (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then

	select	a.nr_seq_escrit,
		b.cd_pessoa_fisica,
		b.cd_cgc
	into STRICT	nr_seq_banco_escrit_w,
		cd_pessoa_fisica_w,
		cd_cgc_w
	from	titulo_pagar b,
		titulo_pagar_escrit a
	where	a.nr_titulo	= b.nr_titulo
	and	a.nr_seq_escrit	= nr_seq_banco_escrit_p
	and	a.nr_titulo	= nr_titulo_pagar_p;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		select	count(*)
		into STRICT	qt_registro_w
		from	inconsistencia_rem_escrit a
		where	a.nr_seq_banco_escrit	= nr_seq_banco_escrit_w
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w;
	elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		select	count(*)
		into STRICT	qt_registro_w
		from	inconsistencia_rem_escrit a
		where	a.nr_seq_banco_escrit	= nr_seq_banco_escrit_w
		and	a.cd_cgc	= cd_cgc_w;
	end if;

	if (qt_registro_w > 0) then
		ie_inconsistente_w	:= 'S';
	else
		ie_inconsistente_w	:= 'N';
	end if;

end if;

return	ie_inconsistente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
