CREATE OR REPLACE FUNCTION obter_incon_prescr (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE

ie_erro_w	smallint;
ie_existe_erro_w	varchar(1);


BEGIN

select	coalesce(max(ie_erro),0)
into STRICT	ie_erro_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia = nr_seq_prescr_p;

if (ie_erro_w > 0) then
	ie_existe_erro_w  := 'S';
else
	ie_existe_erro_w := 'N';
end if;

return	ie_existe_erro_w;

end;
