CREATE OR REPLACE FUNCTION obter_indicacao_ageint ( nr_seq_ageint_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

				
ds_return_w	agenda_integrada.ds_indicacao%type;

/* IE_OPCAO_P
'C' = Agenda de consulta
*/
BEGIN

	if (ie_opcao_p = 'C') then
		select	max(c.ds_indicacao)
		into STRICT	ds_return_w
		from	agenda_consulta a,
				agenda_integrada_item b,
				agenda_integrada c
		where	a.nr_sequencia = b.nr_seq_agenda_cons
		and		c.nr_sequencia = b.nr_seq_agenda_int
		and		a.nr_sequencia = nr_seq_ageint_p;
	end if;

	return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
