CREATE OR REPLACE FUNCTION obter_indice_cut_point ( qt_min_cirurgia_p bigint, nr_seq_proc_interno_p bigint, nr_seq_proced_niss_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_hora_cirur_w	double precision;
qt_cut_point_w	smallint;
qt_retorno_w	smallint := 0;


BEGIN

qt_hora_cirur_w	:= dividir(qt_min_cirurgia_p,60);

if (coalesce(nr_seq_proced_niss_p,0) > 0) then
	select	coalesce(max(qt_cut_point),0)
	into STRICT	qt_cut_point_w
	from	niss_procedimento
	where	nr_sequencia	=	nr_seq_proced_niss_p;
else
	select	coalesce(max(a.qt_cut_point),0)
	into STRICT	qt_cut_point_w
	from	niss_procedimento a,
		proc_interno b
	where	a.nr_sequencia		=	b.nr_seq_proced_niss
	and	b.nr_sequencia		=	nr_seq_proc_interno_p;
end if;

if (qt_hora_cirur_w > qt_cut_point_w) then
	qt_retorno_w := 1;
else
	qt_retorno_w := 0;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
