CREATE OR REPLACE FUNCTION obter_indice_informacao ( nr_sequencia_p bigint, qt_valor_p bigint) RETURNS bigint AS $body$
DECLARE



vl_indice_w	bigint;
C01 CURSOR FOR
	SELECT	vl_indice
	from	PEP_DEST_INF_INDICE
	where	NR_SEQ_DEST_INF	= nr_sequencia_p
	and	qt_valor_p between VL_MINIMO and VL_MAXIMO
	order by 1;



BEGIN
open C01;
loop
fetch C01 into
	vl_indice_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	vl_indice_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
