CREATE OR REPLACE FUNCTION obter_indice_mort_rockall ( ie_opcao_p text, qt_pontuacao_p bigint) RETURNS varchar AS $body$
BEGIN

if (ie_opcao_p = 'C') then 		/*Com ressangramento*/
	if (qt_pontuacao_p < 3) then
		return	'0%';
	elsif (qt_pontuacao_p = 3) then
		return	'5%';
	elsif (qt_pontuacao_p <= 6) then
		return	'5-10%';
	else
		return	'10-35%';
	end if;
elsif (ie_opcao_p = 'S') then  		/*Sem ressangramento*/
	if (qt_pontuacao_p < 3) then
		return	'0%';
	elsif (qt_pontuacao_p = 3) then
		return	'5-10%';
	elsif (qt_pontuacao_p <= 6) then
		return	'15-25%';
	else
		return	'25-50%';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
