CREATE OR REPLACE FUNCTION obter_indice_preco_servico (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


cd_categoria_w			varchar(10);
cd_convenio_w			integer;
cd_edicao_amb_w			integer;
tx_ajuste_w			double precision := 0;
nr_seq_regra_w			bigint := 0;
cd_estabelecimento_w		smallint;
dt_servico_w			timestamp;
cd_servico_w			procedimento_paciente.cd_procedimento%type;

C01 CURSOR FOR
SELECT	b.tx_ajuste_geral
from	preco_servico a,
	convenio_servico b
where	a.cd_tabela_servico	= b.cd_tabela_servico
and	b.cd_convenio		= cd_convenio_w
and	b.cd_estabelecimento = cd_estabelecimento_w
and	b.cd_categoria		= cd_categoria_w
and	trunc(dt_servico_w,'dd')	between b.dt_liberacao_tabela and coalesce(b.dt_termino, dt_servico_w)
and	a.cd_estabelecimento	= cd_estabelecimento_w
and	a.cd_procedimento		= cd_servico_w
and	trunc(dt_servico_w,'dd') between a.dt_inicio_vigencia and coalesce(a.dt_vigencia_final, dt_servico_w)
and 	coalesce(b.ie_situacao,'A')	= 'A'
and (coalesce(a.dt_inativacao::text, '') = '' or a.dt_inativacao > dt_servico_w)
order by	coalesce(b.nr_prioridade,1),
	a.dt_inicio_vigencia desc,
	a.vl_servico desc;


BEGIN

select	nr_seq_ajuste_proc,
	cd_convenio,
	cd_categoria,
	cd_edicao_amb,
	obter_estab_conta_paciente(nr_interno_conta),
	dt_conta,
	cd_procedimento
into STRICT	nr_seq_regra_w,
	cd_convenio_w,
	cd_categoria_w,
	cd_edicao_amb_w,
	cd_estabelecimento_w,
	dt_servico_w,
	cd_servico_w
from	procedimento_paciente
where	nr_sequencia = nr_sequencia_p;

if (nr_seq_regra_w <> 0 AND nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	coalesce(max(tx_ajuste),0)
	into STRICT	tx_ajuste_w
	from	regra_ajuste_proc
	where	nr_sequencia = nr_seq_regra_w;
end if;

if ((coalesce(tx_ajuste_w::text, '') = '') or (tx_ajuste_w = 0)) then
	open C01;
	loop
	fetch C01 into
		tx_ajuste_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			tx_ajuste_w := tx_ajuste_w;
		end;
	end loop;
	close C01;
end if;

return	tx_ajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
