CREATE OR REPLACE FUNCTION obter_indice_reajuste_conc ( nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

c01 CURSOR FOR
SELECT	distinct
	a.ie_indice_reajuste
from	contrato a
where	a.nr_seq_cliente = nr_seq_cliente_p
and	(a.ie_indice_reajuste IS NOT NULL AND a.ie_indice_reajuste::text <> '');

BEGIN
for	r_C01_w in C01 loop
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := r_C01_w.ie_indice_reajuste;
	else
		ds_retorno_w := (ds_retorno_w||', '||r_C01_w.ie_indice_reajuste);
	end if;
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
