CREATE OR REPLACE FUNCTION obter_informacao_atendimento (nr_seq_informacao_p bigint, ie_opcao_p text default 'I') RETURNS varchar AS $body$
DECLARE

/*
IE_OPCAO_P
  I  = DS_INFORMACAO
 C = DS_COR
*/
ds_retorno_w    varchar(255);

BEGIN
if (nr_seq_informacao_p IS NOT NULL AND nr_seq_informacao_p::text <> '') then

	if (ie_opcao_p = 'I') then

		select  max(ds_informacao)
		into STRICT    ds_retorno_w
		from 	informacao_atendimento
		where   nr_sequencia = nr_seq_informacao_p;

	else
		select  max(ds_cor)
		into STRICT    ds_retorno_w
		from 	informacao_atendimento
		where   nr_sequencia = nr_seq_informacao_p;

	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
