CREATE OR REPLACE FUNCTION obter_informacao_ditado (nr_seq_prescr_proc_p bigint, opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno  varchar(255);


BEGIN

if (opcao_p = 'NM') then
  select max(nm_usuario) INTO STRICT ds_retorno from PRESCR_PROC_DITADO where nr_seq_prescr_proc = nr_seq_prescr_proc_p;
else if (opcao_p = 'DT') then
  select to_char(max(dt_atualizacao),'dd/mm/yyyy hh24:mi:ss') INTO STRICT ds_retorno from PRESCR_PROC_DITADO where nr_seq_prescr_proc = nr_seq_prescr_proc_p;
    end if;
end if;

return ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
