SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_informacao_item_wl ( nr_seq_regra_p bigint, ie_escala_p text, cd_tipo_evolucao_p text, nr_seq_disp_pac_p bigint, nr_parecer_p bigint, ie_tipo_diagnostico_p bigint, nr_seq_carta_mae_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_classif_diag_w		classificacao_diagnostico.ds_classificacao%type;
nr_seq_classif_diag_w	wl_regra_item.nr_seq_classif_diag%type;
ie_tipo_diagnostico_w	wl_regra_item.ie_tipo_diagnostico%type;
ie_classif_diag_w		wl_regra_item.ie_classif_diag%type;

ds_retorno_w			varchar(400);


BEGIN

if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	begin
	
	select	ie_classif_diag,
			coalesce(ie_tipo_diagnostico, 0)
	into STRICT	ie_classif_diag_w,
			ie_tipo_diagnostico_w
	from	wl_regra_item
	where	nr_sequencia = nr_seq_regra_p;
	
	if (ie_escala_p IS NOT NULL AND ie_escala_p::text <> '') then
		select	max(obter_valor_dominio(1799,x.ie_escala))
		into STRICT	ds_retorno_w
		from	escala_documentacao x
		where	obter_se_valor_dominio_ativo(1799,x.ie_escala) = 'S'
		and		obter_se_escala_lib_pais(x.nr_sequencia, obter_pais_sistema(wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento)) = 'S'
		and		x.ie_escala = ie_escala_p;
		
	elsif (nr_seq_carta_mae_p IS NOT NULL AND nr_seq_carta_mae_p::text <> '') then
		select	obter_valor_dominio(9030,ie_tipo_pend_carta)
		into STRICT	ds_retorno_w
		from	wl_regra_item
		where	nr_sequencia = nr_seq_regra_p;
		
	elsif (cd_tipo_evolucao_p IS NOT NULL AND cd_tipo_evolucao_p::text <> '') then
		select	ds_tipo_evolucao
		into STRICT	ds_retorno_w
		from	tipo_evolucao
		where	ie_situacao = 'A'
		and		cd_tipo_evolucao = cd_tipo_evolucao_p;
		
	elsif (nr_seq_disp_pac_p IS NOT NULL AND nr_seq_disp_pac_p::text <> '') then
		select	ds_dispositivo
		into STRICT	ds_retorno_w
		from	dispositivo
		where	ie_situacao = 'A'
		and		nr_sequencia = nr_seq_disp_pac_p;
		
	elsif (nr_parecer_p IS NOT NULL AND nr_parecer_p::text <> '') then
		select	ds_tipo_parecer ds
		into STRICT	ds_retorno_w
		from	tipo_parecer
		where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
		and		ie_situacao = 'A'
		and		nr_sequencia = (SELECT	max(x.nr_seq_tipo_parecer)
								from	parecer_medico_req x
								where	x.nr_parecer = nr_parecer_p);
		
	elsif ((ie_tipo_diagnostico_p IS NOT NULL AND ie_tipo_diagnostico_p::text <> '') or (ie_classif_diag_w IS NOT NULL AND ie_classif_diag_w::text <> '')) then
		
		if (ie_classif_diag_w IS NOT NULL AND ie_classif_diag_w::text <> '') then
	
			if (ie_tipo_diagnostico_w > 0) then
				select	obter_valor_dominio(8885, ie_tipo_diagnostico_w) || ' - ' || obter_valor_dominio(9288, ie_classif_diag_w)
				into STRICT	ds_retorno_w
				;
			else
				select	obter_valor_dominio(9288, ie_classif_diag_w)
				into STRICT	ds_retorno_w
				;
			end if;
			
		else
			select	obter_valor_dominio(8885, ie_tipo_diagnostico_p)
			into STRICT	ds_retorno_w
			;
		end if;
	
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_informacao_item_wl ( nr_seq_regra_p bigint, ie_escala_p text, cd_tipo_evolucao_p text, nr_seq_disp_pac_p bigint, nr_parecer_p bigint, ie_tipo_diagnostico_p bigint, nr_seq_carta_mae_p bigint ) FROM PUBLIC;

