SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_informacao_tabela ( nr_atendimento_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


cd_empresa_w			smallint;
cd_estabelecimento_w	smallint;
cd_pessoa_fisica_w		atendimento_paciente.cd_pessoa_fisica%type;
nm_atributo_w			varchar(50);
ds_retorno_w			varchar(2000)	:= '';
ds_valor_atrib_w		varchar(255);
ds_valor_atrib2_w		varchar(255);
qt_reg_w				bigint;
ie_pais_w				valor_dominio.vl_dominio%type;

C01 CURSOR FOR
	SELECT	distinct nm_atributo
	from	pep_dest_inf a
	where	nm_tabela = nm_tabela_p
	and		exists (	SELECT	1
					from	pep_inf_regra x
					where	x.nr_seq_inf	= a.nr_seq_inf);


BEGIN

select	max(cd_pessoa_fisica),
		max(cd_estabelecimento)
into STRICT	cd_pessoa_fisica_w,
		cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

select	max(cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento	= cd_estabelecimento_w;

select 	coalesce(pkg_i18n.get_user_locale, 'pt_BR')
into STRICT 	ie_pais_w
;

open c01;
loop
fetch c01 into
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	SELECT * FROM Obter_Informacao_Atributo(	cd_estabelecimento_w, cd_empresa_w, nr_atendimento_p, cd_pessoa_fisica_w, nm_tabela_p, nm_atributo_w, ds_valor_atrib_w, ds_valor_atrib2_w) INTO STRICT ds_valor_atrib_w, ds_valor_atrib2_w;

	if (nm_tabela_p = 'PAC_CLEREANCE_CREATININA') and (nm_atributo_w = 'QT_CREATININA_SERICA') then
		ds_retorno_w	:= ds_retorno_w || 'DT_CREATININA' ||'='||ds_valor_atrib2_w ||'#@#@';
	end if;

	ds_retorno_w	:= ds_retorno_w || nm_atributo_w ||'='||ds_valor_atrib_w ||'#@#@';
	end;
end loop;
close c01;


if (nm_tabela_p	= 'ATEND_TIMI_RISK_SUPRA') or (nm_tabela_p	= 'ATEND_TIMI_RISK') or (nm_tabela_p	= 'ESCALA_IDV') or (nm_tabela_p	= 'ATEND_PSI')	then
	ds_retorno_w	:= ds_retorno_w || 'QT_ANO' ||'='||Obter_Idade_PF(cd_pessoa_fisica_w,clock_timestamp(),'A') ||'#@#@';
else
	select	count(*)
	into STRICT	qt_reg_w
	from	tabela_atributo
	where	nm_tabela	= nm_tabela_p
	and	nm_atributo	= 'QT_IDADE';
	if (qt_reg_w	> 0) then
		ds_retorno_w	:= ds_retorno_w || 'QT_IDADE' ||'='||Obter_Idade_PF(cd_pessoa_fisica_w,clock_timestamp(),'A') ||'#@#@';
	end if;
end if;

if (nm_tabela_p	= 'ESCALA_SAPS3') then
	ds_retorno_w	:= ds_retorno_w || 'QT_DIAS_INTERNACAO_PREV' ||'='||OBTER_DIAS_INTERNACAO(nr_atendimento_p) ||'#@#@';
elsif (nm_tabela_p	= 'ESCALA_ELPO') then
	ds_retorno_w	:= ds_retorno_w || 'IE_FAIXA_IDADE' ||'='||obter_faixa_idade_escala_elpo(cd_pessoa_fisica_w)||'#@#@';
end if;

if (ie_pais_w = 'pt_BR') then
	ds_retorno_w := replace(ds_retorno_w, '.', ',');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_informacao_tabela ( nr_atendimento_p bigint, nm_tabela_p text) FROM PUBLIC;

