SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_informacoes_domicilio ( cd_domicilio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO
	C = CEP,
	L = LOGRADOURO,
	N = NUMERO,
	CO = COMPLEMENTO
	*/
ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p	= 'C') then
	select	max(a.cd_cep)
	into STRICT	ds_retorno_w
	from	domicilio_familia a
	where	a.cd_domicilio	= cd_domicilio_p;
elsif (ie_opcao_p	= 'L') then
	select	max(a.ds_endereco)
	into STRICT	ds_retorno_w
	from	domicilio_familia a
	where	a.cd_domicilio	= cd_domicilio_p;
elsif (ie_opcao_p	= 'N') then
	select	max(a.nr_endereco)
	into STRICT	ds_retorno_w
	from	domicilio_familia a
	where	a.cd_domicilio	= cd_domicilio_p;
elsif (ie_opcao_p	= 'CO') then
	select	max(a.ds_complemento)
	into STRICT	ds_retorno_w
	from	domicilio_familia a
	where	a.cd_domicilio	= cd_domicilio_p;
else
	ds_retorno_w	:= null;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_informacoes_domicilio ( cd_domicilio_p bigint, ie_opcao_p text) FROM PUBLIC;

