SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_informacoes_item_prescr ( ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, cd_item_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ie_se_necessario_w	varchar(1);
ie_acm_w			varchar(1);
ie_urgencia_w		varchar(1);
ie_agrupador_w		varchar(1);

/*
ITENS:
M	- Medicamento
MAT	- Material
SNE	- Suporte Nutricional Enteral
SOL	- Solução
R	- Recomendação
P	- Procedimento
L	- Exame Laboratorial
*/
/*
RETORNOS:
1	- Se item ACM, SN ou Agora
*/
BEGIN

if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_retorno_p IS NOT NULL AND ie_retorno_p::text <> '') then

	if (ie_tipo_item_p in ('M', 'MAT', 'SNE')) then

		if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') or (cd_item_p IS NOT NULL AND cd_item_p::text <> '') then

			if (ie_tipo_item_p = 'M') then
				ie_agrupador_w := 1;
			elsif (ie_tipo_item_p = 'MAT') then
				ie_agrupador_w := 2;
			elsif (ie_tipo_item_p = 'SNE') then
				ie_agrupador_w := 8;
			end if;

			if (ie_retorno_p = 1) then
				select	coalesce(max(ie_se_necessario),'N'),
						coalesce(max(ie_acm),'N'),
						coalesce(max(ie_urgencia),'N')
				into STRICT	ie_se_necessario_w,
						ie_acm_w,
						ie_urgencia_w
				from	prescr_material
				where	nr_sequencia = coalesce(nr_seq_item_p, nr_sequencia)
				and		ie_agrupador = ie_agrupador_w
				and		cd_material = cd_item_p
				and		nr_prescricao = nr_prescricao_p;

				if ('S' in (ie_se_necessario_w, ie_acm_w, ie_urgencia_w)) then
					ds_retorno_w	:= 'S';
				else
					ds_retorno_w	:= 'N';
				end if;
			end if;

		end if;

	elsif (ie_tipo_item_p = 'SOL') then

		if (ie_retorno_p = 1) then
			select	coalesce(max(ie_se_necessario),'N'),
					coalesce(max(ie_acm),'N'),
					coalesce(max(ie_urgencia),'N')
			into STRICT	ie_se_necessario_w,
					ie_acm_w,
					ie_urgencia_w
			from	prescr_solucao
			where	nr_seq_solucao = nr_seq_item_p
			and		nr_prescricao = nr_prescricao_p;

			if ('S' in (ie_se_necessario_w, ie_acm_w, ie_urgencia_w)) then
				ds_retorno_w	:= 'S';
			else
				ds_retorno_w	:= 'N';
			end if;
		end if;

	elsif (ie_tipo_item_p = 'R') then

		if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') or (cd_item_p IS NOT NULL AND cd_item_p::text <> '') then

			if (ie_retorno_p = 1) then
				select	coalesce(max(ie_se_necessario),'N'),
						coalesce(max(ie_acm),'N'),
						coalesce(max(ie_urgencia),'N')
				into STRICT	ie_se_necessario_w,
						ie_acm_w,
						ie_urgencia_w
				from	prescr_recomendacao
				where	((coalesce(cd_item_p::text, '') = '') or (cd_recomendacao = cd_item_p))
				and		((coalesce(nr_seq_item_p::text, '') = '') or (nr_sequencia = nr_seq_item_p))
				and		nr_prescricao = nr_prescricao_p;

				if ('S' in (ie_se_necessario_w, ie_acm_w, ie_urgencia_w)) then
					ds_retorno_w	:= 'S';
				else
					ds_retorno_w	:= 'N';
				end if;
			end if;

		end if;

	elsif (ie_tipo_item_p in ('P', 'L')) then

		if (ie_retorno_p = 1) then
			select	coalesce(max(ie_se_necessario),'N'),
					coalesce(max(ie_acm),'N'),
					coalesce(max(ie_urgencia),'N')
			into STRICT	ie_se_necessario_w,
					ie_acm_w,
					ie_urgencia_w
			from	prescr_procedimento
			where	nr_sequencia = nr_seq_item_p
			and		nr_prescricao = nr_prescricao_p;

			if ('S' in (ie_se_necessario_w, ie_acm_w, ie_urgencia_w)) then
				ds_retorno_w	:= 'S';
			else
				ds_retorno_w	:= 'N';
			end if;
		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_informacoes_item_prescr ( ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, cd_item_p text, ie_retorno_p text) FROM PUBLIC;

