SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_informacoes_parto ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_parto_normal_w	varchar(1);
ie_parto_forceps_w	varchar(1);
ie_parto_episio_w	varchar(1);
ie_parto_analgesia_w	varchar(1);
ie_parto_cesaria_w	varchar(1);
ds_retorno_w		varchar(255);


BEGIN

select	coalesce(max(ie_parto_normal),'N'),
	coalesce(max(ie_parto_forceps),'N'),
	coalesce(max(ie_parto_episio),'N'),
	coalesce(max(ie_parto_analgesia),'N'),
	coalesce(max(ie_parto_cesaria),'N')
into STRICT	ie_parto_normal_w,
	ie_parto_forceps_w,
	ie_parto_episio_w,
	ie_parto_analgesia_w,
	ie_parto_cesaria_w
from	parto
where	nr_atendimento = nr_atendimento_p;

select	CASE WHEN ie_parto_normal_w='S' THEN 'Normal, ' END  ||
	CASE WHEN ie_parto_forceps_w='S' THEN 'Fórceps, ' END  ||
	CASE WHEN ie_parto_episio_w='S' THEN 'Com epísio, ' END  ||
	CASE WHEN ie_parto_analgesia_w='S' THEN 'Com analgesia, ' END  ||
	CASE WHEN ie_parto_cesaria_w='S' THEN 'Cesárea, ' END
into STRICT	ds_retorno_w
;

if (ie_opcao_p = 'DP') then
	ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
