CREATE OR REPLACE FUNCTION obter_inform_adic_usuario_mx ( nm_usuario_p text, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_jurisdicao_w		inform_adic_usuario.nr_seq_jurisdicao%type;
ie_nivel_usuario_seed_w		inform_adic_usuario.ie_nivel_usuario_seed%type;



BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	max(nr_seq_jurisdicao),
			max(ie_nivel_usuario_seed)
	into STRICT	nr_seq_jurisdicao_w,
			ie_nivel_usuario_seed_w
	from	inform_adic_usuario
	where	nm_usuario	= nm_usuario_p;

	if (ie_tipo_inf_p = 'NR_SEQ_JURISDICAO') then
		ds_retorno_w 	:= nr_seq_jurisdicao_w;

	elsif (ie_tipo_inf_p = 'IE_NIVEL_USUARIO_SEED') then
			ds_retorno_w 	:= ie_nivel_usuario_seed_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
