SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_info_custodia (nr_seq_compl_p bigint, ds_tipo_p text, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		compl_pessoa_fisica_cust.ds_observacao%type;
dt_revisao_w		compl_pessoa_fisica_cust.dt_revisao%type;
nm_usuario_revisao_w    compl_pessoa_fisica_cust.nm_usuario_revisao%type;
ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_seq_compl_p, 0) <> 0) then
	if (ds_tipo_p = 'note')then

		if (PKG_I18N.get_user_locale in ('de_DE', 'de_AT')) then
		
			select	max(ds_observacao)
			into STRICT	ds_observacao_w
			from    compl_pessoa_fisica_cust
			where   nr_sequencia = (SELECT	max(nr_sequencia)
						from    compl_pessoa_fisica_cust
						where 	nr_seq_compl = nr_seq_compl_p
						and cd_pessoa_fisica = cd_pessoa_fisica_p);	
		else
		
			select	max(ds_observacao)
			into STRICT	ds_observacao_w
			from    compl_pessoa_fisica_cust
			where   nr_sequencia = (SELECT	max(nr_sequencia)
						from    compl_pessoa_fisica_cust
						where 	nr_seq_compl = nr_seq_compl_p);	
		end if;
		
		if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '')then
			ds_retorno_w := substr(ds_observacao_w,1,255);
		end if;		

	elsif (ds_tipo_p = 'user')then	
	
	if (PKG_I18N.get_user_locale in ('de_DE', 'de_AT')) then		
	
			select	max(nm_usuario_revisao)
			into STRICT	nm_usuario_revisao_w
			from    compl_pessoa_fisica_cust
			where   nr_sequencia = (SELECT	max(nr_sequencia)
						from    compl_pessoa_fisica_cust
						where 	nr_seq_compl = nr_seq_compl_p
						and cd_pessoa_fisica = cd_pessoa_fisica_p);	

		else		
			select	max(nm_usuario_revisao)
			into STRICT	nm_usuario_revisao_w
			from    compl_pessoa_fisica_cust
			where   nr_sequencia = (SELECT	max(nr_sequencia)
						from    compl_pessoa_fisica_cust
						where 	nr_seq_compl = nr_seq_compl_p);	
		end if;		

		if (nm_usuario_revisao_w IS NOT NULL AND nm_usuario_revisao_w::text <> '')then
			ds_retorno_w := substr(nm_usuario_revisao_w,1,255);
		end if;	
	end if;
end if;


return ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
