SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_info_desfecho (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);


BEGIN
select 	case max(ie_desfecho)
            when 'I' then 'Admission'
            when 'T' then 'Internal transfer'
            when 'A' then 'Discharge'
            else 'Not defined'
        end retorno
into STRICT	ds_retorno_w
from   	atendimento_alta a
where  	a.nr_atendimento = nr_atendimento_p
and    	a.nr_sequencia = (SELECT max(e.nr_sequencia)
			from 	atendimento_alta e
			where 	e.nr_atendimento = a.nr_atendimento
			and   	(e.dt_liberacao IS NOT NULL AND e.dt_liberacao::text <> '')
			and   	coalesce(e.dt_inativacao::text, '') = '');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
