SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_info_equip_man ( nr_seq_equipamento_p bigint, ie_info_p text) RETURNS varchar AS $body$
DECLARE

dt_select_w			timestamp;
ds_select_w			varchar(255);

return_w				varchar(255) := '';


BEGIN

/*
FG 	- Informação se fim garantia
DE 	- Informação se é descarte
DDE - Descrição status descarte
SM 	- Informação se não tem possibilidade de manutenção
DSM - Descrição dos esquipamentos sem possibilidade de manutenção
DA  - Informação se equipamento danificado
DDA - Descrição do dano
*/
if (ie_info_p = 'FG') then

	select	max(dt_fim_garantia)
	into STRICT	dt_select_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

	if (clock_timestamp() > dt_select_w) then
		 return_w := 'S';
	end if;

elsif (ie_info_p = 'DE') or (ie_info_p = 'DDE') then

	select 	coalesce(max(c.ie_descarte),'N'),
			coalesce(max(c.ds_status),'')
	into STRICT 	return_w,
			ds_select_w
	from	equipamento a,
			man_equipamento b,
			man_equip_status c
	where 	a.cd_equipamento = nr_seq_equipamento_p
	and 	a.nr_seq_equipamento_man = b.nr_sequencia
	and 	b.nr_seq_status = c.nr_sequencia
	and		c.ie_descarte = 'S';

	if (ie_info_p = 'DDE') then
		return_w := ds_select_w;
	end if;

elsif (ie_info_p = 'SM') or (ie_info_p = 'DSM') then

	select 	PKG_DATE_UTILS.ADD_MONTH(b.dt_aquisicao, b.qt_tempo_vida_util),
			a.ds_equipamento
	into STRICT 	dt_select_w,
			ds_select_w
	from	equipamento a,
			man_equipamento b
	where 	a.cd_equipamento = nr_seq_equipamento_p
	and 	a.nr_seq_equipamento_man = b.nr_sequencia;

	if (clock_timestamp() > dt_select_w) and (ie_info_p = 'SM') then
		return_w := 'S';
	elsif (clock_timestamp() > dt_select_w) and (ie_info_p = 'DSM') then
		return_w := ds_select_w;
	end if;

elsif (ie_info_p = 'DA') then
	select 	CASE WHEN max(1)=1 THEN 'S'  ELSE 'N' END
	into STRICT 	return_w
	from 	equipamento a,
			man_equipamento b,
			man_tipo_equipamento c,
			man_planej_prev d,
			man_causa_dano e
	where 	a.cd_equipamento = nr_seq_equipamento_p
	and 	a.nr_seq_equipamento_man = b.nr_sequencia
	and 	b.nr_seq_tipo_equip  = c.nr_sequencia
	and		d.nr_seq_tipo_equip  = c.nr_sequencia
	and 	d.nr_seq_causa_dano = e.nr_sequencia;

elsif (ie_info_p = 'DDA') then
	select 	coalesce(max(e.ds_causa), '')
	into STRICT 	return_w
	from 	equipamento a,
			man_equipamento b,
			man_tipo_equipamento c,
			man_planej_prev d,
			man_causa_dano e
	where 	a.cd_equipamento = nr_seq_equipamento_p
	and 	a.nr_seq_equipamento_man = b.nr_sequencia
	and 	b.nr_seq_tipo_equip  = c.nr_sequencia
	and		d.nr_seq_tipo_equip  = c.nr_sequencia
	and 	d.nr_seq_causa_dano = e.nr_sequencia
	and 	e.ie_situacao = 'A';
end if;

return return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
