CREATE OR REPLACE FUNCTION obter_info_estagio (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


nr_seq_estagio_w estagio_status_entrega.nr_sequencia%type;
ie_status_execucao_w prescr_procedimento.ie_status_execucao%type;
ds_retorno_w varchar(255);
nr_regra_w bigint;


BEGIN
ds_retorno_w := '';

select prescr_proc_compl.nr_seq_estagio_entrega, prescr_proc.ie_status_execucao
into STRICT nr_seq_estagio_w, ie_status_execucao_w
from prescr_procedimento prescr_proc
left join prescr_procedimento_compl prescr_proc_compl ON (prescr_proc.nr_seq_proc_compl = prescr_proc_compl.nr_sequencia)
where prescr_proc.nr_prescricao = nr_prescricao_p
and   prescr_proc.nr_sequencia = nr_seq_prescricao_p;

if (coalesce(nr_seq_estagio_w::text, '') = '') then
	nr_regra_w := obter_regra_entrega_apropriada(nr_prescricao_p,nr_seq_prescricao_p);
	nr_seq_estagio_w := obter_estagio_inicial(nr_regra_w, ie_status_execucao_w);
end if;

if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then

	if (ie_opcao_p = 'DS') then
	   select substr(est_entrega.ds_estagio,1,255)
	   into STRICT ds_retorno_w
	   from estagio_entrega est_entrega,
			estagio_status_entrega est_status
	   where est_status.nr_seq_estagio = est_entrega.nr_sequencia
	   and est_status.nr_sequencia = nr_seq_estagio_w;

	end if;

	if (ie_opcao_p = 'COR') then
	   select substr(est_entrega.ds_cor_estagio,1,255)
	   into STRICT ds_retorno_w
	   from estagio_entrega est_entrega,
			estagio_status_entrega est_status
	   where est_status.nr_seq_estagio = est_entrega.nr_sequencia
	   and est_status.nr_sequencia = nr_seq_estagio_w;

	end if;

	if (ie_opcao_p = 'CD') then
	   select substr(est_entrega.nr_sequencia,1,255)
	   into STRICT ds_retorno_w
	   from estagio_entrega est_entrega,
			estagio_status_entrega est_status
	   where est_status.nr_seq_estagio = est_entrega.nr_sequencia
	   and est_status.nr_sequencia = nr_seq_estagio_w;

	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
