CREATE OR REPLACE FUNCTION obter_info_prescr_mat_alt (nr_sequencia_p text, cd_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_campo_p IS NOT NULL AND cd_campo_p::text <> '') then

	if (cd_campo_p = 'cd_intervalo') then

		select max(cd_intervalo)
		into STRICT ds_retorno_w
		from prescr_mat_alteracao_comp
		where nr_sequencia_prescr = nr_sequencia_p;

	elsif (cd_campo_p = 'nr_seq_motivo_adm') then

		select 	max(ds_motivo)
		into STRICT 	ds_retorno_w
		from 	prescr_mat_alteracao_comp a,
				adep_motivo_admnistracao b
		where	a.nr_seq_motivo_adm = b.nr_sequencia
		and		nr_sequencia_prescr = nr_sequencia_p;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
