CREATE OR REPLACE FUNCTION obter_info_setor_exame_js ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_setor_coleta_w	varchar(150);
ds_setor_entrega_w	varchar(150);
ds_observacao_w		varchar(255);

ds_retorno_w		varchar(1000);


BEGIN

select	substr(obter_nome_setor(cd_setor_entrega),1,150) ds_setor_entrega,
	substr(obter_nome_setor(cd_setor_coleta),1,150) ds_setor_coleta,
	substr(ds_observacao_coleta,1,255) ds_observacao
into STRICT	ds_setor_coleta_w,
	ds_setor_entrega_w,
	ds_observacao_w
from 	prescr_procedimento
where 	nr_prescricao 	= nr_prescricao_p
and 	nr_sequencia 	= nr_sequencia_p;

if (ds_setor_entrega_w IS NOT NULL AND ds_setor_entrega_w::text <> '') then
	ds_retorno_w := substr(obter_desc_expressao(298391) ||': '|| ds_setor_entrega_w || '\n\n',1,1000);
else
	ds_retorno_w := substr(obter_desc_expressao(298391) ||': '|| '\n\n ',1,1000);
end if;

if (ds_setor_coleta_w IS NOT NULL AND ds_setor_coleta_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w || obter_desc_expressao(298389) ||': ' || ds_setor_coleta_w || '\n\n',1,1000);
else
	ds_retorno_w := substr(ds_retorno_w || obter_desc_expressao(298389) ||': ' || '\n\n',1,1000);
end if;

if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w || obter_desc_expressao(779443) ||': ' || ds_observacao_w,1,1000);
else
	ds_retorno_w := substr(ds_retorno_w || obter_desc_expressao(779443) ||': ',1,1000);
end if;

return	ds_retorno_w;

end;
