CREATE OR REPLACE FUNCTION obter_info_w_item_prescr (nr_prescricao_p bigint, nr_seq_item_p bigint, ie_origem_inf_p text, tp_informacao_p text) RETURNS varchar AS $body$
DECLARE


resultado_w	varchar(30);


BEGIN

if (tp_informacao_p = 'D') then

	select	max(qt_dose)
	into STRICT	resultado_w
	from 	w_item_prescr
	where  	nr_prescricao	= nr_prescricao_p
	and		nr_seq_item		= nr_seq_item_p
	and		ie_origem_inf	= ie_origem_inf_p;

elsif (tp_informacao_p = 'U') then

	select	max(cd_unidade_medida)
	into STRICT	resultado_w
	from 	w_item_prescr
	where  	nr_prescricao	= nr_prescricao_p
	and		nr_seq_item		= nr_seq_item_p
	and		ie_origem_inf	= ie_origem_inf_p;

end if;

return	resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
