CREATE OR REPLACE FUNCTION obter_inf_adic_item ( nr_seq_prescricao_p bigint, nr_seq_item_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

select		CASE WHEN ie_tipo_campo='N' THEN to_char(nr_inf_adic) WHEN ie_tipo_campo='A' THEN ds_inf_adic WHEN ie_tipo_campo='D' THEN to_char(dt_inf_adic,'dd/mm/yyyy hh24:mi:ss') END
into STRICT		ds_retorno_w
from		rep_inf_adic_item d,
		rep_inf_adic_ppi a,
		prescr_procedimento c,
		rep_inf_adic_pp b
where		a.nr_seq_inf_adic		= b.nr_sequencia
and		b.nr_prescricao	  		= c.nr_prescricao
and		b.nr_seq_item	  		= c.nr_sequencia
and		d.nr_sequencia			= a.nr_seq_item_inf_adic
and		a.nr_seq_item_inf_adic		= nr_seq_item_p
and		c.nr_prescricao			= nr_seq_prescricao_p
and		c.nr_sequencia			= nr_seq_item_prescricao_p;

return		ds_retorno_w;

end;
$body$
