CREATE OR REPLACE FUNCTION obter_inf_adic_prescr ( nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE



cd_procedimento_w		bigint;
ie_origem_proced_w 		bigint;
nr_seq_exame_w			bigint;
nr_seq_proc_interno_w		bigint;
nr_seq_inf_adic_w		bigint;

cd_grupo_proc_w			bigint;
cd_especialidade_w		integer;
cd_area_procedimento_w		integer;
ie_tipo_w			varchar(5);
nr_seq_classif_w		bigint;


c01 CURSOR FOR
	SELECT	nr_seq_inf_adic
	from	rep_regra_inf_adic
	where	coalesce(cd_procedimento,coalesce(cd_procedimento_w,0))		=	coalesce(cd_procedimento_w,0)
	and	coalesce(ie_origem_proced,coalesce(ie_origem_proced_w,0))		=	coalesce(ie_origem_proced_w,0)
	and	coalesce(nr_seq_exame,coalesce(nr_seq_exame_w,0))			=	coalesce(nr_seq_exame_w,0)
	and	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_w,0)) 	=	coalesce(nr_seq_proc_interno_w,0)
	and	coalesce(cd_grupo_proc,coalesce(cd_grupo_proc_w,0))		=	coalesce(cd_grupo_proc_w,0)
	and	coalesce(cd_especialidade,coalesce(cd_especialidade_w,0))		=	coalesce(cd_especialidade_w,0)
	and	coalesce(cd_area_procedimento,coalesce(cd_area_procedimento_w,0)) =	coalesce(cd_area_procedimento_w,0)
	and	coalesce(ie_tipo,coalesce(ie_tipo_w,0)) 				=	coalesce(ie_tipo_w,0)
	and	coalesce(nr_seq_classif,coalesce(nr_seq_classif_w,0)) 		=	coalesce(nr_seq_classif_w,0)
	order by coalesce(cd_procedimento,0),
		coalesce(nr_seq_exame,0),
		coalesce(nr_seq_proc_interno,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0),
		coalesce(ie_tipo,'A'),
		coalesce(nr_seq_classif,0);


BEGIN

select	cd_procedimento,
	ie_origem_proced,
	nr_seq_exame,
	nr_seq_proc_interno
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_exame_w,
	nr_seq_proc_interno_w
from	prescr_procedimento
where	nr_prescricao	=	nr_prescricao_p
and	nr_sequencia	=	nr_seq_item_p;

select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento		=	cd_procedimento_w
and	ie_origem_proced	=	ie_origem_proced_w;

if (nr_seq_proc_interno_w > 0) then
	select	max(ie_tipo),
		max(nr_seq_classif)
	into STRICT	ie_tipo_w,
		nr_seq_classif_w
	from	proc_interno
	where	nr_sequencia	=	nr_seq_proc_interno_w;
end if;

open C01;
loop
fetch C01 into
	nr_seq_inf_adic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_inf_adic_w := nr_seq_inf_adic_w;
	end;
end loop;
close C01;

return	nr_seq_inf_adic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
