CREATE OR REPLACE FUNCTION obter_inf_assinatura_digital (nr_seq_assinatura_p bigint, ie_opcao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_seq_assinatura_p,0) > 0) then
	if (ie_opcao_p = 0) then
		select	to_char(max(dt_assinatura),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_retorno_w
		from	TASY_ASSINATURA_DIGITAL a
		where	a.nr_sequencia	= nr_seq_assinatura_p;
	elsif (ie_opcao_p = 1) then
		select	cd_classif_relat ||','|| cd_relatorio
		into STRICT	ds_retorno_w
		from	TASY_ASSINATURA_DIGITAL a
		where	a.nr_sequencia	= nr_seq_assinatura_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
