CREATE OR REPLACE FUNCTION obter_inf_aval_nutric (nr_atendimento_p bigint, ie_informacao_p text) RETURNS bigint AS $body$
DECLARE

/*
ie_informacao_p
FA - Fator atividade
FS - Fator stress
*/
nr_seq_fator_ativ_w		bigint;
nr_seq_fator_stress_w		bigint;


BEGIN

if (ie_informacao_p	= 'FA') then

	select	max(nr_seq_fator_ativ)
	into STRICT	nr_seq_fator_ativ_w
	from	aval_nutricao
	where	nr_atendimento	= nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	(nr_seq_fator_ativ IS NOT NULL AND nr_seq_fator_ativ::text <> '');

	return	nr_seq_fator_ativ_w;

elsif (ie_informacao_p	= 'FS') then

	select	max(nr_seq_fator_stress)
	into STRICT	nr_seq_fator_stress_w
	from	aval_nutricao
	where	nr_atendimento	= nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	(nr_seq_fator_stress IS NOT NULL AND nr_seq_fator_stress::text <> '');

	return	nr_seq_fator_stress_w;

end if;

end;
