SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_cat_embarazo (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*
 Retorno
 - 0 - N.E.
 - 1 - Gestação
 - 2 - Parto
 - 3 - Pós-parto
 - 4 - 43 dias à 11 meses depois do parto
 - 5 - sem gravidez nos últimos 11 meses
 - 8 - Não se aplica
*/
ds_retorno_w			varchar(255)	:= '0';
qt_atendimento_gravidez_w	bigint;
cd_pessoa_fisica_w		atendimento_paciente.cd_pessoa_fisica%type;
dt_nascimento_w			nascimento.dt_nascimento%type;
dt_fim_parto_w			parto.dt_fim_parto%type;
dt_obito_w			declaracao_obito.dt_obito%type;
ie_obito_mulher_w		declaracao_obito.ie_obito_mulher%type;



BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_p;

	select	count(1)
	into STRICT	qt_atendimento_gravidez_w
	from	atendimento_gravidez
	where	nr_atendimento	= nr_atendimento_p;

	select	max(b.dt_nascimento)
	into STRICT	dt_nascimento_w
	from	atendimento_paciente a,
		nascimento b
	where	a.cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and	a.nr_atendimento	= b.nr_atendimento;

	select	max(b.dt_fim_parto)
	into STRICT	dt_fim_parto_w
	from	atendimento_paciente a,
		parto b
	where	a.cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and	a.nr_atendimento	= b.nr_atendimento;

	begin
		select	dt_obito,
			ie_obito_mulher
		into STRICT	dt_obito_w,
			ie_obito_mulher_w
		from	declaracao_obito
		where	nr_atendimento	= nr_atendimento_p;
	exception
	when others then
		dt_obito_w		:= null;
		ie_obito_mulher_w	:= null;
	end;

	if (ie_obito_mulher_w = 'S') and (obter_idade_pf(cd_pessoa_fisica_w,dt_obito_w,'A') between 10 and 54) then
		if (qt_atendimento_gravidez_w = 0) and (coalesce(dt_nascimento_w::text, '') = '') and (coalesce(dt_fim_parto_w::text, '') = '') then
			ds_retorno_w := '0';
		elsif (qt_atendimento_gravidez_w > 0) and (coalesce(dt_nascimento_w::text, '') = '') and (coalesce(dt_fim_parto_w::text, '') = '') then
			ds_retorno_w := '1';
		elsif (dt_obito_w - coalesce(dt_nascimento_w,dt_fim_parto_w) = 0) then
			ds_retorno_w := '2';
		elsif (dt_obito_w - coalesce(dt_nascimento_w,dt_fim_parto_w) < 43) then
			ds_retorno_w := '3';
		elsif (dt_obito_w - coalesce(dt_nascimento_w,dt_fim_parto_w) < 330) then
			ds_retorno_w := '4';
		else
			ds_retorno_w := '5';
		end if;
	else
		ds_retorno_w := '0';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
