CREATE OR REPLACE FUNCTION obter_inf_consignado ( nr_seq_age_consig_p bigint, ie_opcao_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	      varchar(255);
ie_dispensado_w	         varchar(1);
ie_retirada_w	            varchar(1);
qt_material_w	            double precision;
ds_cme_preparado_w   varchar(255);
ie_tipo_pessoa_w         varchar(15);


BEGIN

select	max(ds_observacao),
	max(ie_dispensado),
	max(qt_material),
	max(ds_cme_preparado),
	coalesce(max(ie_retirada),'N'),
   max(ie_tipo_pessoa)
into STRICT	ds_observacao_w,
         ie_dispensado_w,
         qt_material_w,
         ds_cme_preparado_w,
         ie_retirada_w,
         ie_tipo_pessoa_w
from	agenda_pac_consig_inf
where	nr_seq_age_consig 	= nr_seq_age_consig_p
and	ie_tipo_informacao	= ie_tipo_p;

if (ie_opcao_p = 'O') then
	return	ds_observacao_w;
elsif (ie_opcao_p = 'D') then
	return ie_dispensado_w;
elsif (ie_opcao_p = 'Q') then
	return qt_material_w;
elsif (ie_opcao_p = 'P') then
	return ds_cme_preparado_w;
elsif (ie_opcao_p = 'R') then
	return ie_retirada_w;
elsif (ie_opcao_p = 'TP') then
   return ie_tipo_pessoa_w;
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
