CREATE OR REPLACE FUNCTION obter_inf_conta_paciente ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_interno_conta_w	bigint;
cd_responsavel_w	bigint;
ds_retorno_w		varchar(255)	:= null;


BEGIN 
 
/* 
R - Nome responsavel 
D - descrição da Etapa 
*/
 
 
select	max(nr_interno_conta),max(cd_responsavel) 
into STRICT	nr_interno_conta_w, 
	cd_responsavel_w 
from	conta_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if (coalesce(nr_interno_conta_w,0) > 0) then 
begin 
	if (ie_opcao_p = 'R') then 
		ds_retorno_w := obter_nome_pessoa_fisica(cd_responsavel_w,null);
	elsif (ie_opcao_p = 'D') then 
		ds_retorno_w := obter_conta_paciente_etapa(nr_interno_conta_w, 'D');
	end if;
end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
