SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_dieta_adep (nr_prescricao_p bigint, nr_seq_dieta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_dieta_w		varchar(80);
ds_obs_dieta_w		varchar(4000);
ds_inf_w		varchar(2000);
ie_suspenso_w		varchar(2);
dt_suspensao_w		timestamp;
qt_horas_jejum_w	smallint;
qt_parametro_w		double precision;
ds_um_w			varchar(255);

c01 CURSOR FOR
SELECT	substr(obter_nome_dieta(cd_dieta),1,80),
	ds_observacao,
	coalesce(ie_suspenso,'N'),
	dt_suspensao ,
	qt_horas_jejum,
	qt_parametro,
	substr(obter_unid_med_dieta(cd_dieta),1,150)
from	prescr_dieta
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia	= coalesce(nr_seq_dieta_p,nr_sequencia)
order by
	nr_sequencia;	


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	open c01;
	loop
	fetch c01 into	
		ds_dieta_w,
		ds_obs_dieta_w,
		ie_suspenso_w,
		dt_suspensao_w,
		qt_horas_jejum_w,
		qt_parametro_w,
		ds_um_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_opcao_p = 'D') and (ds_dieta_w IS NOT NULL AND ds_dieta_w::text <> '') and (ie_suspenso_w = 'N')	then
			ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w || chr(10),1,2000);
		elsif (ie_opcao_p = 'O') and (ds_obs_dieta_w IS NOT NULL AND ds_obs_dieta_w::text <> '') then
			  ds_inf_w := substr(ds_inf_w || ' - ' || obter_desc_expressao(692668) || ds_obs_dieta_w || chr(10),1,2000);
		elsif (ie_opcao_p = 'D') and (ds_dieta_w IS NOT NULL AND ds_dieta_w::text <> '') and (ie_suspenso_w = 'S')	then
			  ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w || ' - '||obter_desc_expressao(298988)|| to_char(dt_suspensao_w, 'dd/mm/yyyy hh24:mi:ss') || chr(10),1,2000);		
		elsif (ie_opcao_p = 'DI') and (ds_dieta_w IS NOT NULL AND ds_dieta_w::text <> '') and (ie_suspenso_w = 'N')	then
			if (qt_horas_jejum_w IS NOT NULL AND qt_horas_jejum_w::text <> '') then
				ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w || obter_desc_expressao(346208, 'dt_validade_w='||qt_horas_jejum_w)||' hr'||chr(10),1,2000);		
			else
				ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w ||chr(10),1,2000);
			end if;
		elsif (ie_opcao_p = 'DIO') and (ds_dieta_w IS NOT NULL AND ds_dieta_w::text <> '') and (ie_suspenso_w = 'N')	then
			if (qt_horas_jejum_w IS NOT NULL AND qt_horas_jejum_w::text <> '') then
				ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w ||obter_desc_expressao(346208, 'dt_validade_w='||qt_horas_jejum_w)||' hr'||chr(10),1,2000);		
			else
				ds_inf_w := substr(ds_inf_w || ' - ' || ds_dieta_w ||chr(10),1,2000);
			end if;
			if (ds_obs_dieta_w IS NOT NULL AND ds_obs_dieta_w::text <> '') then
				ds_inf_w := substr(ds_inf_w || chr(10)||obter_desc_expressao(692668) ||ds_obs_dieta_w||chr(10),1,2000);
			end if;
		end if;
		end;
		
		if (qt_parametro_w > 0) and (qt_parametro_w < 1) then
			ds_inf_w := substr('0' || qt_parametro_w || ' ' ||ds_um_w || ' - ' ||  ds_inf_w,1,2000);
		elsif (qt_parametro_w > 0) then
			ds_inf_w := substr(qt_parametro_w || ' ' ||ds_um_w || ' - ' ||  ds_inf_w,1,2000);
		end if;
	end loop;
	close c01;
end if;

return ds_inf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
