CREATE OR REPLACE FUNCTION obter_inf_estadia ( nr_atendimento_p bigint, nr_seq_episodio_p bigint, ie_informacao_p text) RETURNS bigint AS $body$
DECLARE


	ds_retorno_w double precision;


BEGIN

	if (ie_informacao_p = 'MAX') then
		select	max(qt_estadia_max)
		into STRICT	ds_retorno_w
		from	episodio_paciente_drg
		where	ie_situacao = 'A'
		and		(((nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') and nr_seq_episodio_paciente = nr_seq_episodio_p)
		or ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and nr_atendimento = nr_atendimento_p));

	elsif (ie_informacao_p = 'MIN') then
		select	max(qt_estadia_min)
		into STRICT	ds_retorno_w
		from	episodio_paciente_drg
		where	ie_situacao = 'A'
		and		(((nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') and nr_seq_episodio_paciente = nr_seq_episodio_p)
		or ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and nr_atendimento = nr_atendimento_p));
    elsif (ie_informacao_p = 'MED') then
		select	max(obter_dados_drg(nr_seq_drg_proc,'MS'))
		into STRICT	ds_retorno_w
		from	episodio_paciente_drg
		where	ie_situacao = 'A'
		and		(((nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') and nr_seq_episodio_paciente = nr_seq_episodio_p)
		or ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and nr_atendimento = nr_atendimento_p));
	end if;

return ds_retorno_w;

end;
$body$
