CREATE OR REPLACE FUNCTION obter_inf_evol_gravidade (nr_atendimento_p integer, ie_opcao_p text default 'G') RETURNS varchar AS $body$
DECLARE


nr_seq_evol_grav_w	integer(10);
retorno_w			varchar(255);


BEGIN

select	max(nr_seq_evol_grav)
into STRICT	nr_seq_evol_grav_w
from	evolucao_paciente
where	cd_evolucao = (	SELECT	max(cd_evolucao)
						from	evolucao_paciente
						where	nr_atendimento = nr_atendimento_p
						and		(nr_seq_evol_grav IS NOT NULL AND nr_seq_evol_grav::text <> ''));
if (nr_seq_evol_grav_w IS NOT NULL AND nr_seq_evol_grav_w::text <> '') then
	if (ie_opcao_p = 'G') then
		retorno_w := to_char(nr_seq_evol_grav_w);
	else
		retorno_w := obter_informacao_gravidade(nr_seq_evol_grav_w,ie_opcao_p);
	end if;
end if;

return	retorno_w;

end;
$body$
