SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_exames_interno (nr_seq_avaliacao_p bigint, nr_prescricao_p bigint, nr_exames_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_procedimento_w	varchar(2000) := '';
ds_retorno_w	varchar(2000) := '';

c01 CURSOR FOR
SELECT	substr(Obter_Desc_Prescr_Proc(cd_procedimento,ie_origem_proced, nr_seq_proc_interno, 'N'),1,80),
		nr_sequencia
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	obter_avaliacao_procedimento(cd_procedimento, ie_origem_proced, nr_seq_proc_interno) = nr_seq_avaliacao_p
and coalesce(nr_seq_avaliacao::text, '') = ''
and	obter_se_contido(nr_sequencia,	nr_exames_p) = 'S'
group by substr(Obter_Desc_Prescr_Proc(cd_procedimento,ie_origem_proced, nr_seq_proc_interno, 'N'),1,80),
	nr_sequencia;


BEGIN
open c01;
loop
fetch c01 into
	ds_procedimento_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_tipo_p = 1) then
		ds_retorno_w := ds_retorno_w || ds_procedimento_w || ', ';
	else
		ds_retorno_w := ds_retorno_w || nr_sequencia_w || ', ';
	end if;
	end;
end loop;
close c01;

return substr(ds_retorno_w, 1, length(ds_retorno_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
