SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_exame_rotina (nr_seq_rotina_p bigint) RETURNS varchar AS $body$
DECLARE


ds_informacao_w		varchar(4000);
ds_prescricao_w		varchar(4000);

c01 CURSOR FOR
SELECT	ds_prescricao
from	exame_lab_rotina
where	nr_seq_rotina = nr_seq_rotina_p

union all

SELECT	ds_prescricao
from	procedimento_rotina
where	nr_seq_rotina = nr_seq_rotina_p
order by 1 desc;
			

BEGIN

open c01;
loop
	fetch c01 into
		ds_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	
	if length(ds_prescricao_w || chr(13) || ds_informacao_w) > 4000-3 then
		ds_informacao_w := substr(ds_informacao_w, 1, 4000-3) || '...';
		exit;
	end if;
	
	ds_informacao_w	:= ds_prescricao_w || chr(13) || ds_informacao_w;
end loop;
close c01;

return	ds_informacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
