CREATE OR REPLACE FUNCTION obter_inf_funcao_medico ( cd_funcao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
SA - Sequencia de apresentação
*/
ds_retorno_w		varchar(255);
nr_seq_apresentacao_w	bigint;


BEGIN

if (cd_funcao_p > 0) then
	select	nr_seq_apresentacao
	into STRICT	nr_seq_apresentacao_w
	from	funcao_medico
	where	cd_funcao	= cd_funcao_p;

	if (ie_opcao_p = 'SA') then
		ds_retorno_w	:= nr_seq_apresentacao_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
