CREATE OR REPLACE FUNCTION obter_inf_mat_medic_desc ( ie_informacao_p text, cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE


/*
IE_INFORMACAO_P
	35 |Campo "Modo de administração"
	25 |Campo "Reação adversa"
	30 |Campo "Modo de preparo"
	20 |Campo "Dosagem"
	15 |Campo "Precaução"
	10 |Campo "Contra-indicação"
	5   |Campo "Indicação"
	90 |Campo "Bibliogradia"
*/
ds_retorno_w			varchar(4000);
nr_seq_ficha_tecnica_w	medic_ficha_tecnica.nr_sequencia%type;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (ie_informacao_p IS NOT NULL AND ie_informacao_p::text <> '') then
	select	max(nr_seq_ficha_tecnica)
	into STRICT	nr_seq_ficha_tecnica_w
	from	material
	where	cd_material = cd_material_p;

	if (nr_seq_ficha_tecnica_w IS NOT NULL AND nr_seq_ficha_tecnica_w::text <> '') then
			select	ds_descritivo
			into STRICT	ds_retorno_w
			from	medic_descritivo
			where	nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w
			and		ie_tipo = ie_informacao_p;
	end if;
end if;

return	substr(ds_retorno_w,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_inf_mat_medic_desc ( ie_informacao_p text, cd_material_p bigint ) FROM PUBLIC;

